package eu.stratosphere.api.java.record.functions;import eu.stratosphere.api.common.functions.AbstractFunction;import eu.stratosphere.api.common.functions.GenericReducer;import eu.stratosphere.types.Record;import eu.stratosphere.utils.Collector;import java.util.Iterator;/** * TODO * * @author Administrator * @version 1.0 * @date 2020/12/26 23:44 */public abstract class ReduceFunction extends AbstractFunction implements GenericReducer<Record, Record> {    @Override    public abstract void reduce(Iterator<Record> records, Collector<Record> out) throws Exception;    @Override    public void combine(Iterator<Record> records, Collector<Record> out) throws Exception {        reduce(records, out);    }}